Le système $\left(\mathcal{S}_m\right)$ est homogène. Il admet des solutions non nulles si et seulement si $\mathrm{det} (A_m )= 0 \iff m \in\{-1,0,1\}$.

Pour $m=0$ :
$$
\begin{aligned}
& \left(\mathcal{S}_0\right) \Leftrightarrow\left\{\begin{array} { r l } 
{ - x + y - z } & { = 0 } \\
{ z } & { = 0 } \\
{ x - y - z } & { = 0 }
\end{array} \Leftrightarrow \left\{\begin{array}{l}
x=y \\
z=0
\end{array}\right.\right. \\
& \text { Sol }=\{(x, x, 0) \in \mathbb{R}^3 \, \mid \, x \in \mathbb{R}\} \\
&
\end{aligned}
$$
Pour $m=1$ :
$$
\begin{aligned}
& \left(\mathcal{S}_1\right) \Leftrightarrow\left\{\begin{array} { r l } 
{ 0 } & { = 0 } \\
{ y + z } & { = 0 } \\
{ - y - z } & { = 0 }
\end{array} \Leftrightarrow \left\{\begin{array}{l}
x \text { quelconque } \\
y=-z
\end{array}\right.\right. \\
& \text { Sol }=\{(x, y,-y) \in \mathbb{R}^3 \, \mid \, x, y \in \mathbb{R}\} \\
&
\end{aligned}
$$
Pour $m=-1$ :
$$
\begin{aligned}
&\left(\mathcal{S}_{-1}\right) \Leftrightarrow\left\{\begin{array}{r}
-2 x+2 y=0 \\
-y+z=0 \\
2 x-y-z=0
\end{array}\right. \\
& \Leftrightarrow\left\{\begin{array}{l}
x=y \\
y=z
\end{array}\right. \\
& \text { Sol }=\{(x, x, x) \in \mathbb{R}^3 \, \mid \, x \in \mathbb{R}\}
\end{aligned}
$$

\textbf{Joueur $A$:} 
		\[ X\sim\mathcal{N}(\mu,\sigma), \quad \prob(X\geq 19)=0.977, \quad \prob(X\leq 22)=0.84\]
		En posant $Z=\frac{X-\mu}{\sigma}$, on a $Z\sim \mathcal{N}(0,1)$ et:
		\[ \begin{cases}
			\prob\left(Z\geq \frac{19-\mu}{\sigma} \right)=0.977 \\
			\prob\left( Z \leq \frac{22-\mu}{\sigma}\right)=0.84
		\end{cases}
		\quad \Leftrightarrow \quad
		\begin{cases}
			\prob\left(Z\leq -\frac{19-\mu}{\sigma} \right)=0.977 \\
			\prob\left( Z \leq \frac{22-\mu}{\sigma}\right)=0.84
		\end{cases}
		\]
		et par lecture de la table de loi $\mathcal{N}(0,1)$, on obtient:
		$$\begin{align*}
			\begin{cases} -\frac{19-\mu}{\sigma}=2 \\
				\frac{22-\mu}{\sigma}=1
			\end{cases}
			\quad \Leftrightarrow \quad 
			\begin{cases} -19+\mu=2\sigma \\
				22-\mu=\sigma
			\end{cases}
			\quad \Leftrightarrow \quad 
			\begin{cases} \mu=21 \\
				\sigma=1
			\end{cases}
			\quad \Leftrightarrow \quad 
			X\sim \mathcal{N}(21,\sigma=1)
		\end{align*}$$
		\vspace{2em}

		\textbf{Joueur $B$:} 
		\[ Y\sim\mathcal{N}(\mu,\sigma), \quad \prob(Y\geq 19)=0.745, \quad \prob(Y\leq 22)=0.908\]
		En posant $Z=\frac{Y-\mu}{\sigma}$, on a $Z\sim \mathcal{N}(0,1)$ et:
		\[ \begin{cases}
			\prob\left(Z\geq \frac{19-\mu}{\sigma} \right)=0.745 \\
			\prob\left( Z \leq \frac{22-\mu}{\sigma}\right)=0.908
		\end{cases}
		\quad \Leftrightarrow \quad
		\begin{cases}
			\prob\left(Z\leq -\frac{19-\mu}{\sigma} \right)=0.745 \\
			\prob\left( Z \leq \frac{22-\mu}{\sigma}\right)=0.908
		\end{cases}
		\]
		et par lecture de la table de loi $\mathcal{N}(0,1)$, on obtient:
		$$\begin{align*}
			\begin{cases} -\frac{19-\mu}{\sigma}=0.66 \\
				\frac{22-\mu}{\sigma}=1.33
			\end{cases}
			\quad \Leftrightarrow \quad 
			\begin{cases} -19+\mu=0.66\sigma \\
				22-\mu=1.33\sigma
			\end{cases}
			\quad \Leftrightarrow \quad 
			\begin{cases} \mu=20 \\
				\sigma=1.5
			\end{cases}
			\quad \Leftrightarrow \quad 
			Y\sim \mathcal{N}(20,\sigma=1.5)
		\end{align*}$$

