@import url('https://fonts.googleapis.com/css2?family=Nunito:wght@400;600;700&family=Source+Sans+Pro:wght@400;600&display=swap');


/* @import '@fontsource/fira-mono'; */
@import 'bootstrap/dist/css/bootstrap.min.css';

:root {
	--font-primary: 'Source Sans Pro', sans-serif;
	--font-heading: 'Nunito', sans-serif;
	--color-bg-0: rgb(211, 211, 211);
	--color-bg-1: hsl(0, 0%, 87%);
	--color-bg-2: hsl(224, 12%, 68%);
	--color-theme-1: #ff3e00;
	--color-theme-2: #910000;
	--color-text: rgba(0, 0, 0, 0.7);
	--column-width: 42rem;
	--column-margin-top: 4rem;
	font-family: var(--font-body);
	color: var(--color-text);
    --bs-primary: #49B2B2;
}





body {
	min-height: 100vh;
	margin: 0;
	background-attachment: fixed;
	background-color: var(--color-bg-1);
	background-size: 100vw 100vh;
	background-image: radial-gradient(
			50% 50% at 50% 50%,
			rgba(255, 255, 255, 0.75) 0%,
			rgba(2, 39, 28, 0) 100%
		),
		linear-gradient(180deg, var(--color-bg-0) 0%, var(--color-bg-1) 15%, var(--color-bg-2) 50%);
}

body {
	font-family: var(--font-primary);
  }

  h1, h2, h3, h4, h5, h6 {
	font-family: var(--font-heading);
  }

pre {
	font-size: 16px;
	font-family: var(--font-mono);
	background-color: rgba(255, 255, 255, 0.45);
	border-radius: 3px;
	box-shadow: 2px 2px 6px rgb(255 255 255 / 25%);
	padding: 0.5em;
	overflow-x: auto;
	color: var(--color-text);
}

.text-column {
	display: flex;
	max-width: 48rem;
	flex: 0.6;
	flex-direction: column;
	justify-content: center;
	margin: 0 auto;
}

input,
button {
	font-size: inherit;
	font-family: inherit;
}

button:focus:not(:focus-visible) {
	outline: none;
}

@media (min-width: 720px) {
	h1 {
		font-size: 2.4rem;
	}
}

.visually-hidden {
	border: 0;
	clip: rect(0 0 0 0);
	height: auto;
	margin: 0;
	overflow: hidden;
	padding: 0;
	position: absolute;
	width: 1px;
	white-space: nowrap;

}

.tags {
    display: flex;
    flex-wrap: wrap;
    gap: 8px;
  }
  .tag {
    background-color: #e0e0e0; /* Couleur de fond du tag */
    color: #333; /* Couleur du description du tag */
    padding: 5px 10px; /* Espacement autour du description */
    border-radius: 15px; /* Coins arrondis pour un effet de badge */
    font-size: 14px; /* Taille de la police */
    font-weight: 500; /* Épaisseur du description */
    display: inline-block;
    cursor: pointer;
    transition: background-color 0.3s ease, color 0.3s ease;
  }

  .tag:hover {
    background-color: #333; /* Couleur de fond au survol */
    color: #fff; /* Couleur du description au survol */
  }
  .btn-primary {
    background-color: var(--bs-primary) !important;
    border-color: var(--bs-primary) !important;
}



.btn-primary:hover {
    background-color: darken(var(--bs-primary), 10%) !important;
    border-color: darken(var(--bs-primary), 10%) !important;
}

.btn-primary:focus,
.btn-primary.focus {
    box-shadow: 0 0 0 0.2rem rgba(145, 0, 0, 0.5) !important;
}

.btn-outline-primary {
    color: var(--bs-primary) !important; /* Texte */
    border-color: var(--bs-primary) !important; /* Bordure */
    background-color: transparent !important; /* Fond transparent */
    transition: color 0.3s ease, background-color 0.3s ease, border-color 0.3s ease;
}

.btn-outline-primary:hover {
    color: #fff !important; /* Texte blanc au survol */
    background-color: var(--bs-primary) !important; /* Fond couleur principale */
    border-color: var(--bs-primary) !important; /* Bordure couleur principale */
}

.btn-outline-primary:focus,
.btn-outline-primary.focus {
    box-shadow: 0 0 0 0.2rem rgba(145, 0, 0, 0.5) !important; /* Ombre couleur principale */
}

.btn-outline-primary:disabled,
.btn-outline-primary.disabled {
    color: rgba(145, 0, 0, 0.5) !important; /* Texte désactivé */
    border-color: rgba(145, 0, 0, 0.5) !important; /* Bordure désactivée */
    background-color: transparent !important; /* Fond transparent */
}

.text-primary {
    color: var(--bs-primary) !important; /* Utilise votre couleur principale */
}

.btn-add {
